mapper.insert:
INSERT INTO borad(borad_id, borad_title,borad_contents, borad_name, borad_time)
	VALUES(
		   #{borad_id, jdbcType=NUMERIC}
		 , #{borad_title}
		 , #{borad_contents}
		 , #{borad_name}
		 , now()
	);

mapper.update:
	UPDATE borad SET 
	borad_title = #{borad_title}
   ,borad_contents = #{borad_contents} 
	WHERE 
	borad_id = #{borad_id};
